mapper.select:

SELECT a.*, g.leader_nick FROM active a, group_info g

	a.group_no = g.group_no
	AND active_no = #{activeNo}

mapper.update:
UPDATE active
active_name = #{activeName}, active_start_date = #{activeStartDate}, active_end_date = #{activeEndDate}, active_x = #{activeX}, active_y = #{activeY}, active_start_time = #{activeStartTime}, active_end_time = #{activeEndTime}, active_info = #{activeInfo}, active_local = #{activeLocal},
active_url = #{activeUrl}, active_short_url = #{activeShortUrl}
active_no = #{activeNo}
